using System.Threading.Tasks;
using OpenVASP.CSharpClient.Internals.Messages;
using OpenVASP.CSharpClient.Internals.Services;
using Xunit;

namespace OpenVASP.Tests
{
    public class WhisperFormatterTest
    {
        [Theory]
        [InlineData(MessageType.SessionRequest, "", "ea7e31149fa6c297b1ba13ff128ca37079127d9c4cf3c753a6ef7070e0af1e68", "023ecbd9076c71f1ad7bb6059a620a73b1b3e397d2d8f18fdc4737927afcfe84d8")]
        [InlineData(MessageType.TransferRequest, "", "751450ae7d126e11e2c5eb9ad78c589a6c633fb5afd076511960e035453fa1d4", "0344b229c5038ca2b0f63c7c5ac20e9961c3de84c3a0c65dd3ecfb74aef1399e84")]
        [InlineData(MessageType.TransferReply, "", "751450ae7d126e11e2c5eb9ad78c589a6c633fb5afd076511960e035453fa1d4", "028a4135364ff6c662bd7c81b60ed55eeeb91f8bb2b7faa13846cf2d32dbe0e8da")]
        [InlineData(MessageType.Termination, "", "751450ae7d126e11e2c5eb9ad78c589a6c633fb5afd076511960e035453fa1d4", "0344b229c5038ca2b0f63c7c5ac20e9961c3de84c3a0c65dd3ecfb74aef1399e84")]
        [InlineData(MessageType.Abort, "", "751450ae7d126e11e2c5eb9ad78c589a6c633fb5afd076511960e035453fa1d4", "028a4135364ff6c662bd7c81b60ed55eeeb91f8bb2b7faa13846cf2d32dbe0e8da")]
        public void MessageFormatterServiceTest(MessageType type, string payload, string aesKeyHex, string signingKey)
        {
            var signService = new MessageSignService(string.Empty);
            var formatterService = new MessageFormatterService(signService, string.Empty);
            var (messageContent, bodyString, sig) = formatterService.Deserialize(payload, aesKeyHex, signingKey);
            
            Assert.Equal(type, messageContent.Header.MessageType);
        }
    }
}